import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import { Link } from 'react-router-dom';

const Venda = () => {
  const [values, setValues] = useState({ name: '', quantidade: '' });
  const [relatorio, setRelatorio] = useState([]);
  const [produtos, setProdutos] = useState([]);

  const alterarValores = (e) => {
    const { name, value } = e.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  const createClickButton = async (e) => {
    e.preventDefault();
    if (!values.name || !values.quantidade) {
      alert('Preencha todos os campos');
      return;
    }
    try {
      const response = await api.post('/vendaProduto', {
        nameProduto: values.name,
        quantidadeProduto: parseInt(values.quantidade),
        usuario: sessionStorage.getItem('usuario'),
      });
      alert(response.data.message);
      setValues({ name: '', quantidade: '' });
      listaVenda();
      listaProdutos();
    } catch (error) {
      alert(error.response?.data?.message || 'Falha ao vender produto');
    }
  };

  const listaVenda = async () => {
    try {
      const response = await api.get('/relatorioVenda');
      setRelatorio(response.data.produtos || []);
    } catch (error) {
      alert('Erro ao carregar vendas');
    }
  };

  const listaProdutos = async () => {
    try {
      const response = await api.get('/produtos'); // Precisamos criar essa rota
      setProdutos(response.data || []);
    } catch (error) {
      console.error('Erro ao carregar produtos', error);
    }
  };

  useEffect(() => {
    listaVenda();
    listaProdutos();
  }, []);

  return (
    <div className="Produto--container">
      <div className="register--container">
        <nav style={{ marginBottom: '20px', textAlign: 'center' }}>
          <Link to="/" style={linkStyle}>Login</Link> | 
          <Link to="/produto" style={linkStyle}>Produtos</Link> | 
          <Link to="/usuario" style={linkStyle}>Usuários</Link>
        </nav>

        <h2>Venda de Produtos</h2>

        <form onSubmit={createClickButton}>
          <label>Produto</label>
          <select
            name="name"
            value={values.name}
            onChange={alterarValores}
            required
            style={inputStyle}
          >
            <option value="">Selecione um produto</option>
            {produtos.map((p) => (
              <option key={p.id} value={p.nameProduto}>
                {p.nameProduto} (Estoque: {p.quantidadeProduto})
              </option>
            ))}
          </select>

          <br /><br />

          <label>Quantidade</label>
          <input
            type="number"
            name="quantidade"
            placeholder="Quantidade"
            value={values.quantidade}
            onChange={alterarValores}
            required
            style={inputStyle}
          />

          <br /><br />

          <button type="submit" className="register--buttton">Vender</button>
        </form>

        <br />
        <hr />

        <h2>Relatório de Vendas</h2>
        <button onClick={listaVenda} className="register--buttton">Atualizar</button>

        <table style={{ width: '100%', marginTop: '10px' }}>
          <thead>
            <tr style={{ backgroundColor: '#f0f0f0' }}>
              <th>Produto</th>
              <th>Quantidade</th>
              <th>Usuário</th>
              <th>Data/Hora</th>
            </tr>
          </thead>
          <tbody>
            {relatorio.length === 0 ? (
              <tr>
                <td colSpan="4" style={{ textAlign: 'center', padding: '20px' }}>
                  Nenhuma venda registrada
                </td>
              </tr>
            ) : (
              relatorio.map((v, k) => (
                <tr key={k}>
                  <td>{v.nameProduto}</td>
                  <td>{v.quantidadeProduto}</td>
                  <td>{v.usuario}</td>
                  <td>{new Date(v.dataHora).toLocaleString()}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

const linkStyle = {
  margin: '0 10px',
  color: '#2575fc',
  textDecoration: 'none'
};

const inputStyle = {
  width: '100%',
  padding: '10px',
  border: '1px solid #ccc',
  borderRadius: '8px',
  fontSize: '16px'
};

export default Venda;
