import React from 'react';
import { useState, useEffect } from 'react';
import api from '../../services/api';

const Venda = () => {
  const [values, setValues] = useState({
    name: '',
    quantidade: '',
  });
  const [relatorio, setRelatorio] = useState([]);

  const alterarValores = (event) => {
    const { name, value } = event.target;
    setValues((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const createClickButton = async (event) => {
    event.preventDefault();
    try {
      const response = await api.post('/vendaProduto', {
        nameProduto: values.name,
        quantidadeProduto: values.quantidade,
        usuario: sessionStorage.getItem('usuario'),
      });
      alert(response.data.message);
    } catch (error) {
      alert('Falha ao registrar venda');
    }
  };

  const listaVenda = async () => {
    try {
      const response = await api.get('/relatorioVenda');
      setRelatorio(response.data.produtos || []);
    } catch (error) {
      console.error('Erro ao buscar relatório de vendas', error);
      setRelatorio([]);
    }
  };

  useEffect(() => {
    listaVenda();
  }, []);

  return (
    <div className="Produto--container">
      <div className="registroProduto--container">
        <h2>Venda de Produtos</h2>
        <br />
        <label>Nome do Produto</label>
        <input
          type="text"
          name="name"
          placeholder="Digite o nome do Produto"
          value={values.name}
          onChange={alterarValores}
          required
        />

        <br />
        <label>Quantidade</label>
        <input
          type="number"
          name="quantidade"
          placeholder="Digite a quantidade do Produto"
          value={values.quantidade}
          onChange={alterarValores}
          required
        />

        <br />
        <br />

        <button className="register--buttton" onClick={createClickButton}>
          Vender
        </button>

        <br />
        <br />

        <hr />

        <h2>Lista de Vendas de Produtos</h2>

        <button className="register--buttton" onClick={listaVenda}>
          Consultar Venda
        </button>

        <br />
        <br />

        <table>
          <thead>
            <tr>
              <th>Produto</th>
              <th>Quantidade</th>
              <th>Usuário</th>
              <th>Data/Hora</th>
            </tr>
          </thead>
          <tbody>
            {relatorio.map((val, key) => (
              <tr key={key}>
                <td>{val.nameProduto}</td>
                <td>{val.quantidadeProduto}</td>
                <td>{val.usuario}</td>
                <td>{new Date(val.dataHora).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Venda;
