import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Venda = () => {
  const [values, setValues] = useState({ name: '', quantidade: '' });
  const [relatorio, setRelatorio] = useState([]);
  const [produtos, setProdutos] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const nome = values.name;
    const quantidade = parseInt(values.quantidade);

    if (!nome || isNaN(quantidade) || quantidade <= 0) {
      alert('Selecione um produto e quantidade válida');
      return;
    }

    try {
      const response = await api.post('/vendaProduto', {
        nameProduto: nome,
        quantidadeProduto: quantidade,
        usuario: sessionStorage.getItem('usuario'),
      });
      alert(response.data.message);
      setValues({ name: '', quantidade: '' });
      loadVendas();
      loadProdutos();
    } catch (error) {
      alert(error.response?.data?.message || 'Falha ao vender produto');
    }
  };

  const loadVendas = async () => {
    try {
      const response = await api.get('/relatorioVenda');
      setRelatorio(Array.isArray(response.data?.produtos) ? response.data.produtos : []);
    } catch (error) {
      alert('Erro ao carregar vendas');
    }
  };

  const loadProdutos = async () => {
    try {
      const response = await api.get('/produtos');
      setProdutos(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      alert('Erro ao carregar produtos');
    }
  };

  useEffect(() => {
    loadVendas();
    loadProdutos();
  }, []);

  return (
    <div>
      <nav className="navbar">
        <a href="/" className="navbar-brand">
          <i className="fas fa-box"></i> Estoque Fácil
        </a>
        <div className="navbar-nav">
          <a href="/">Login</a>
          <a href="/produto">Produtos</a>
          <a href="/usuario">Usuários</a>
          <a href="/venda">Vendas</a>
        </div>
      </nav>

      <div className="container-main">
        <div className="page-header">
          <h1>Venda de Produtos</h1>
          <p>Registre uma nova venda e atualize o estoque</p>
        </div>

        <div className="card">
          <h2>Registrar Venda</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Produto</label>
              <select
                name="name"
                value={values.name}
                onChange={handleChange}
                required
              >
                <option value="">Selecione um produto</option>
                {produtos.map((p) => (
                  <option key={p.id} value={p.nameProduto}>
                    {p.nameProduto} (Estoque: {p.quantidadeProduto})
                  </option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label>Quantidade</label>
              <input
                type="number"
                name="quantidade"
                value={values.quantidade}
                onChange={handleChange}
                placeholder="1"
                required
                min="1"
              />
            </div>
            <button type="submit">Vender</button>
          </form>
        </div>

        <div className="card">
          <h2>Relatório de Vendas</h2>
          <button onClick={loadVendas} style={{ marginBottom: '1rem' }}>Atualizar</button>
          {relatorio.length === 0 ? (
            <p className="message">Nenhuma venda registrada.</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Produto</th>
                  <th>Qtd</th>
                  <th>Usuário</th>
                  <th>Data/Hora</th>
                </tr>
              </thead>
              <tbody>
                {relatorio.map((v, k) => (
                  <tr key={k}>
                    <td>{v.nameProduto}</td>
                    <td>{v.quantidadeProduto}</td>
                    <td>{v.usuario}</td>
                    <td>{new Date(v.dataHora).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>

      <footer>
        <p>Sistema de Controle de Estoque &copy; 2025</p>
      </footer>
    </div>
  );
};

export default Venda;
