import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Venda = () => {
  const [values, setValues] = useState({ name: '', quantidade: '' });
  const [relatorio, setRelatorio] = useState([]);
  const [produtos, setProdutos] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const nome = values.name;
    const quantidade = parseInt(values.quantidade);

    if (!nome || isNaN(quantidade) || quantidade <= 0) {
      alert('Selecione um produto e quantidade válida');
      return;
    }

    try {
      const response = await api.post('/vendaProduto', {
        nameProduto: nome,
        quantidadeProduto: quantidade,
        usuario: sessionStorage.getItem('usuario'),
      });
      alert(response.data.message);
      setValues({ name: '', quantidade: '' });
      loadVendas();
      loadProdutos();
    } catch (error) {
      alert(error.response?.data?.message || 'Falha ao vender produto');
    }
  };

  const loadVendas = async () => {
    try {
      const response = await api.get('/relatorioVenda');
      const vendas = Array.isArray(response.data?.produtos) ? response.data.produtos : [];
      setRelatorio(vendas);
    } catch (error) {
      console.error('Erro ao carregar vendas', error);
    }
  };

const loadProdutos = async () => {
  try {
    console.log('🔹 Iniciando carregamento de produtos (Venda)...');
    const response = await api.get('/produtos');
    console.log('📦 Resposta da API:', response);

    if (!response.data) {
      throw new Error('Dados vazios');
    }

    const validos = Array.isArray(response.data) ? response.data : [];
    setProdutos(validos);
  } catch (error) {
    console.error('❌ Erro ao carregar produtos:', error);
    if (error.response) {
      console.error('Servidor respondeu com erro:', error.response.data);
      alert(`Erro: ${error.response.data.message || 'Falha ao carregar produtos'}`);
    } else if (error.request) {
      alert('❌ Sem resposta do servidor. Verifique se o backend está rodando em http://localhost:3001');
    } else {
      alert('❌ Erro: ' + error.message);
    }
  }
};

  useEffect(() => {
    loadVendas();
    loadProdutos();
  }, []);

  return (
    <div className="Produto--container">
      <div className="registroProduto--container">
        <nav>
          <a href="/">Login</a> | 
          <a href="/usuario">Usuários</a> | 
          <a href="/produto">Produtos</a> | 
          <a href="/venda">Vendas</a>
        </nav>
        <h2>Venda de Produtos</h2>
        <form onSubmit={handleSubmit}>
          <label>Produto</label>
          <select
            name="name"
            value={values.name}
            onChange={handleChange}
            required
          >
            <option value="">Selecione um produto</option>
            {produtos.length === 0 ? (
              <option disabled>Nenhum produto disponível</option>
            ) : (
              produtos.map(p => (
                <option key={p.id} value={p.nameProduto}>
                  {p.nameProduto} (Estoque: {p.quantidadeProduto})
                </option>
              ))
            )}
          </select>
          <br /><br />
          <label>Quantidade</label>
          <input
            type="number"
            name="quantidade"
            placeholder="Quantidade"
            value={values.quantidade}
            onChange={handleChange}
            required
            min="1"
          />
          <br /><br />
          <button className="register--buttton" type="submit">Vender</button>
        </form>

        <br />
        <hr />
        <h2>Relatório de Vendas</h2>
        <button onClick={loadVendas} className="register--buttton">Atualizar</button>

        <table>
          <thead>
            <tr>
              <th>Produto</th>
              <th>Qtd</th>
              <th>Usuário</th>
              <th>Data/Hora</th>
            </tr>
          </thead>
          <tbody>
            {relatorio.length === 0 ? (
              <tr>
                <td colSpan="4" style={{ textAlign: 'center', padding: '20px' }}>
                  Nenhuma venda registrada
                </td>
              </tr>
            ) : (
              relatorio.map((v, k) => (
                <tr key={k}>
                  <td>{v.nameProduto}</td>
                  <td>{v.quantidadeProduto}</td>
                  <td>{v.usuario}</td>
                  <td>{new Date(v.dataHora).toLocaleString()}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Venda;
