import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Venda = () => {
  const [values, setValues] = useState({ name: '', quantidade: '' });
  const [vendas, setVendas] = useState([]);
  const [produtos, setProdutos] = useState([]);
  const [periodo, setPeriodo] = useState('todos'); // <-- estado do filtro

  // Mudar valores dos inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };

  // Enviar venda
  const handleSubmit = async (e) => {
    e.preventDefault();
    const nome = values.name;
    const quantidade = parseInt(values.quantidade);

    if (!nome || isNaN(quantidade) || quantidade <= 0) {
      alert('Preencha corretamente');
      return;
    }

    try {
      const response = await api.post('/vendaProduto', {
        nameProduto: nome,
        quantidadeProduto: quantidade,
        usuario: sessionStorage.getItem('usuario'),
      });
      alert(response.data.message);
      setValues({ name: '', quantidade: '' });
      carregarVendas();
      carregarProdutos();
    } catch (error) {
      alert('Erro ao vender');
    }
  };

  // Excluir venda
  const handleDelete = async (nameProduto) => {
    if (!window.confirm(`Excluir todas as vendas de "${nameProduto}"? Isso devolverÃ¡ o estoque.`)) return;

    try {
      const response = await api.delete(`/venda?nameProduto=${encodeURIComponent(nameProduto)}`);
      alert(response.data.message);
      carregarVendas();
      carregarProdutos();
    } catch (error) {
      alert('Erro ao excluir');
    }
  };

  // Carregar vendas
  const carregarVendas = async () => {
    try {
      const response = await api.get('/relatorioVenda');
      setVendas(Array.isArray(response.data?.produtos) ? response.data.produtos : []);
    } catch (error) {
      console.log('Erro ao carregar vendas');
    }
  };

  // Carregar produtos
  const carregarProdutos = async () => {
    try {
      const response = await api.get('/produtos');
      setProdutos(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      console.log('Erro ao carregar produtos');
    }
  };

  // Efeito: carrega dados ao abrir
  useEffect(() => {
    carregarVendas();
    carregarProdutos();
  }, []);

  // --- FILTRO DE PERÃODO ---
  const hoje = new Date();
  const vendasFiltradas = vendas.filter(v => {
    const data = new Date(v.dataHora);

    switch (periodo) {
      case 'hoje':
        return data.toDateString() === hoje.toDateString();
      case 'esta-semana':
        const diaSemana = hoje.getDay();
        const inicio = new Date(hoje);
        inicio.setDate(hoje.getDate() - diaSemana);
        return data >= inicio;
      case 'este-mes':
        return data.getMonth() === hoje.getMonth() && data.getFullYear() === hoje.getFullYear();
      default:
        return true;
    }
  });

  // --- VALOR TOTAL ---
  const totalValor = vendasFiltradas.reduce((sum, v) => sum + v.valorTotal, 0);

  return (
    <div style={container}>
      {/* Navbar */}
      <nav style={navStyle}>
        <div style={navBrand}>ðŸ”· Estoque FÃ¡cil</div>
        <div style={navLinks}>
          <a href="/produto" style={linkStyle}>Produtos</a>
          <a href="/venda" style={linkStyle}>Vendas</a>
          <span style={navUser}>OlÃ¡, {sessionStorage.getItem('usuario')}</span>
          <button onClick={() => {
            sessionStorage.removeItem('usuario');
            window.location.href = '/';
          }} style={logoutButton}>
            Sair
          </button>
        </div>
      </nav>

      <main style={main}>
        <h2 style={title}>Registrar Venda</h2>

        <form onSubmit={handleSubmit} style={form}>
          <div style={inputGroup}>
            <label>Produto</label>
            <select
              name="name"
              value={values.name}
              onChange={handleChange}
              required
              style={selectStyle}
            >
              <option value="">Selecione...</option>
              {produtos.map(p => (
                <option key={p.id} value={p.nameProduto}>
                  {p.nameProduto} (R$ {p.valor?.toFixed(2)}) - Estoque: {p.quantidadeProduto}
                </option>
              ))}
            </select>
          </div>
          <div style={inputGroup}>
            <label>Qtd</label>
            <input
              type="number"
              name="quantidade"
              value={values.quantidade}
              onChange={handleChange}
              placeholder="1"
              required
              min="1"
              style={inputStyle}
            />
          </div>
          <button type="submit" style={btnPrimary}>Vender</button>
        </form>

        <h2 style={title}>HistÃ³rico de Vendas</h2>

        {/* Filtro */}
