import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Produto = () => {
  const [values, setValues] = useState({ name: '', quantidade: '' });
  const [produtos, setProdutos] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const nome = values.name.trim();
    const quantidade = parseInt(values.quantidade);

    if (!nome || isNaN(quantidade) || quantidade < 0) {
      alert('Nome e quantidade válidos são obrigatórios');
      return;
    }

    try {
      const response = await api.post('/cadastroProduto', {
        nameProduto: nome,
        quantidadeProduto: quantidade,
      });
      alert(response.data.message);
      setValues({ name: '', quantidade: '' });
      loadProdutos();
    } catch (error) {
      alert('Falha ao cadastrar produto');
    }
  };

const handleDelete = async (nameProduto) => {
  if (!window.confirm(`Deseja excluir o produto "${nameProduto}"?`)) return;

  try {
    const response = await api.delete('/produto', {
      data: { nameProduto }  // ✅ Assim sim: data dentro da config
    });
    alert(response.data.message);
    loadProdutos();
  } catch (error) {
    alert(error.response?.data?.message || 'Falha ao excluir produto');
  }
};

  useEffect(() => {
    loadProdutos();
  }, []);

  return (
    <div>
      <nav className="navbar">
        <a href="/" className="navbar-brand">
          <i className="fas fa-box"></i> Estoque Fácil
        </a>
        <div className="navbar-nav">
          <span style={{ color: '#555', marginRight: '1rem' }}>
            Olá, <strong>{sessionStorage.getItem('usuario')}</strong>
          </span>
          <a href="/" onClick={(e) => {
            e.preventDefault();
            if (window.confirm('Deseja sair?')) {
              sessionStorage.removeItem('usuario');
              window.location.href = '/';
            }
          }}>Logout</a>
        </div>
      </nav>

      <div className="container-main">
        <div className="page-header">
          <h1>Gerenciamento de Produtos</h1>
          <p>Cadastre, atualize e exclua produtos do estoque</p>
        </div>

        <div className="card">
          <h2>Cadastrar Produto</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Nome do Produto</label>
              <input
                type="text"
                name="name"
                value={values.name}
                onChange={handleChange}
                placeholder="Ex: Caneta, Caderno..."
                required
              />
            </div>
            <div className="form-group">
              <label>Quantidade em Estoque</label>
              <input
                type="number"
                name="quantidade"
                value={values.quantidade}
                onChange={handleChange}
                placeholder="0"
                required
                min="0"
              />
            </div>
            <button type="submit">Cadastrar / Atualizar</button>
          </form>
        </div>

        <div className="card">
          <h2>Produtos Cadastrados</h2>
          {produtos.length === 0 ? (
            <p className="message">Nenhum produto cadastrado.</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Produto</th>
                  <th>Estoque</th>
                  <th>Status</th>
                  <th>Ação</th>
                </tr>
              </thead>
              <tbody>
                {produtos.map((p) => (
                  <tr key={p.id}>
                    <td>{p.nameProduto}</td>
                    <td>{p.quantidadeProduto}</td>
                    <td>
                      {p.quantidadeProduto <= 0 ? (
                        <span style={{ color: 'red', fontWeight: 'bold' }}>❌ Esgotado</span>
                      ) : p.quantidadeProduto < 5 ? (
                        <span style={{ color: '#ff9800', fontWeight: 'bold' }}>⚠️ Estoque baixo</span>
                      ) : (
                        <span style={{ color: 'green' }}>✅ Disponível</span>
                      )}
                    </td>
                    <td>
                      <button
                        onClick={() => handleDelete(p.nameProduto)}
                        style={{
                          background: '#f44336',
                          color: 'white',
                          border: 'none',
                          padding: '6px 10px',
                          borderRadius: '4px',
                          cursor: 'pointer'
                        }}
                      >
                        Excluir
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>

      <footer>
        <p>Sistema de Controle de Estoque &copy; 2025</p>
      </footer>
    </div>
  );
};

export default Produto;
