import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Venda = () => {
  const [values, setValues] = useState({ name: '', quantidade: '' });
  const [relatorio, setRelatorio] = useState([]);
  const [produtos, setProdutos] = useState([]);
  const [periodo, setPeriodo] = useState('todos');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const nome = values.name;
    const quantidade = parseInt(values.quantidade);

    if (!nome || isNaN(quantidade) || quantidade <= 0) {
      alert('Selecione um produto e quantidade v√°lida');
      return;
    }

    try {
      const response = await api.post('/vendaProduto', {
        nameProduto: nome,
        quantidadeProduto: quantidade,
        usuario: sessionStorage.getItem('usuario'),
      });
      alert(response.data.message);
      setValues({ name: '', quantidade: '' });
      loadVendas();
      loadProdutos();
    } catch (error) {
      alert(error.response?.data?.message || 'Falha ao vender produto');
    }
  };

  const handleDelete = async (nameProduto) => {
    if (!window.confirm('Deseja excluir todas as vendas deste produto?')) return;

    try {
      // 
      const response = await api.delete(`/venda?nameProduto=${encodeURIComponent(nameProduto)}`);
      alert(response.data.message);
      carregarVendas();
      carregarProdutos();
    } catch (error) {
      alert('Erro ao excluir venda');
    }
  };

  const loadVendas = async () => {
    try {
      const response = await api.get('/relatorioVenda');
      setRelatorio(Array.isArray(response.data?.produtos) ? response.data.produtos : []);
    } catch (error) {
      alert('Erro ao carregar vendas');
    }
  };

  const loadProdutos = async () => {
    try {
      const response = await api.get('/produtos');
      setProdutos(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      alert('Erro ao carregar produtos');
    }
  };

  useEffect(() => {
    loadVendas();
    loadProdutos();
  }, []);

  // Filtra as vendas pelo per√≠odo selecionado
  const hoje = new Date();
  const vendasFiltradas = relatorio.filter(v => {
    const data = new Date(v.dataHora);

    switch (periodo) {
      case 'hoje':
        return data.toDateString() === hoje.toDateString();
      case 'esta-semana':
        const diaSemana = hoje.getDay();
        const inicio = new Date(hoje);
        inicio.setDate(hoje.getDate() - diaSemana);
        return data >= inicio;
      case 'este-mes':
        return data.getMonth() === hoje.getMonth() && data.getFullYear() === hoje.getFullYear();
      default:
        return true;
    }
  });

  // Valor total das vendas filtradas
  const totalValor = vendasFiltradas.reduce((sum, v) => sum + v.valorTotal, 0);

  return (
    <div>
      {/* Navbar */}
      <nav style={navStyle}>
        <div style={navBrand}>üî∑ Estoque F√°cil</div>
        <div style={navLinks}>
          <a href="/produto" style={navLink}>Produtos</a>
          <a href="/venda" style={navLink}>Vendas</a>
          <span style={navUser}>Ol√°, {sessionStorage.getItem('usuario')}</span>
          <button onClick={() => {
            sessionStorage.removeItem('usuario');
            window.location.href = '/';
          }} style={logoutButton}>
            Sair
          </button>
        </div>
      </nav>

      <div style={container}>
        <h2 style={title}>Registrar Venda</h2>

        <form onSubmit={handleSubmit} style={form}>
          <div style={inputGroup}>
            <label>Produto</label>
            <select
              name="name"
              value={values.name}
              onChange={handleChange}
              required
              style={selectStyle}
            >
              <option value="">Selecione um produto</option>
              {produtos.map((p) => (
                <option key={p.id} value={p.nameProduto}>
                  {p.nameProduto} (Estoque: {p.quantidadeProduto}) - R$ {p.valor?.toFixed(2) || '0.00'}
                </option>
              ))}
            </select>
          </div>
          <div style={inputGroup}>
            <label>Quantidade</label>
            <input
              type="number"
              name="quantidade"
              value={values.quantidade}
              onChange={handleChange}
              placeholder="1"
              required
              min="1"
              style={inputStyle}
            />
          </div>
          <button type="submit" style={buttonStyle}>Vender</button>
        </form>

        <h2 style={title}>Hist√≥rico de Vendas</h2>

        {/* Filtro por per√≠odo */}
        <div style={filtroContainer}>
          <label>Filtrar por per√≠odo:</label>
          <select value={periodo} onChange={(e) => setPeriodo(e.target.value)} style={filtroSelect}>
            <option value="todos">Todos</option>
            <option value="hoje">Hoje</option>
            <option value="esta-semana">Esta Semana</option>
            <option value="este-mes">Este M√™s</option>
          </select>
        </div>

        {/* Resumo das vendas */}
        <div style={resumoVendas}>
          <strong>Total de vendas exibidas:</strong> {vendasFiltradas.length}
          <br />
          <strong>Valor total: R$</strong>{' '}
          {totalValor.toFixed(2)}
        </div>

        <table style={tableStyle}>
          <thead>
            <tr style={{ backgroundColor: '#f0f0f0' }}>
              <th>Produto</th>
              <th>Qtd</th>
              <th>Valor Unit.</th>
              <th>Total</th>
              <th>Usu√°rio</th>
              <th>Data</th>
              <th>A√ß√£o</th>
            </tr>
          </thead>
          <tbody>
            {vendasFiltradas.length === 0 ? (
              <tr>
                <td colSpan="7" style={{ textAlign: 'center', padding: '20px' }}>
                  Nenhuma venda encontrada para este per√≠odo
                </td>
              </tr>
            ) : (
              vendasFiltradas.map((v, k) => (
                <tr key={k}>
                  <td>{v.nameProduto}</td>
                  <td>{v.quantidadeProduto}</td>
                  <td>R$ {parseFloat(v.valorUnitario || 0).toFixed(2)}</td>
                  <td>R$ {parseFloat(v.valorTotal || 0).toFixed(2)}</td>
                  <td>{v.usuario}</td>
                  <td>{new Date(v.dataHora).toLocaleString()}</td>
                  <td>
                    <button
                      onClick={() => handleDelete(v.nameProduto)}
                      style={deleteButton}
                    >
                      Excluir
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      <footer style={footerStyle}>
        Sistema de Controle de Estoque - Projeto Acad√™mico ADS
      </footer>
    </div>
  );
};

