import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/auth/login', { email, password });
      sessionStorage.setItem('usuario', response.data.name);
      alert('Login realizado com sucesso!');
      navigate('/produto');
    } catch (error) {
      alert('Email ou senha inválidos');
    }
  };

  return (
    <div style={container}>
      <div style={card}>
        <h2 style={title}>Entrar no Sistema</h2>
        <p style={subtitle}>Controle de Estoque - ADS</p>

        <form onSubmit={handleLogin} style={form}>
          <div style={inputGroup}>
            <label style={labelStyle}>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="seu@email.com"
              required
              style={inputStyle}
            />
          </div>

          <div style={inputGroup}>
            <label style={labelStyle}>Senha</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Sua senha"
              required
              style={inputStyle}
            />
          </div>

          <button type="submit" style={btnPrimary}>Entrar</button>
        </form>

        <p style={linkSection}>
          Não tem conta?{' '}
          <a href="/usuario" style={link}>
            Cadastre-se
          </a>
        </p>
      </div>

      <footer style={footerStyle}>
        Sistema de Controle de Estoque - Projeto Acadêmico ADS
      </footer>
    </div>
  );
};

// === ESTILOS ===
const container = {
  minHeight: '100vh',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
  alignItems: 'center',
  backgroundColor: '#f0f4f8',
  padding: '20px'
};

const card = {
  backgroundColor: 'white',
  padding: '30px',
  borderRadius: '10px',
  width: '100%',
  maxWidth: '400px',
  boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
  textAlign: 'center'
};

const title = {
  color: '#003366',
  marginBottom: '5px',
  fontSize: '24px'
};

const subtitle = {
  color: '#666',
  fontSize: '14px',
  marginBottom: '20px'
};

const form = {
  display: 'flex',
  flexDirection: 'column',
  gap: '15px'
};

const inputGroup = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'flex-start'
};

const labelStyle = {
  fontSize: '14px',
  color: '#555',
  marginBottom: '5px'
};

const inputStyle = {
  width: '100%',
  padding: '10px',
  border: '1px solid #ccc',
  borderRadius: '6px',
  fontSize: '14px'
};

const btnPrimary = {
  background: '#003366',
  color: 'white',
  border: 'none',
  padding: '12px',
  borderRadius: '6px',
  fontSize: '16px',
  cursor: 'pointer',
  marginTop: '10px'
};

const linkSection = {
  marginTop: '20px',
  fontSize: '14px',
  color: '#555'
};

const link = {
  color: '#0066cc',
  textDecoration: 'none'
};

const footerStyle = {
  textAlign: 'center',
  margin: '40px 0 20px',
  fontSize: '12px',
  color: '#777'
};

export default Login;
