import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/auth/login', { email, password });
      sessionStorage.setItem('usuario', email);
      navigate('/produto');
    } catch (error) {
      alert(error.response?.data?.message || 'E-mail ou senha inválidos');
    }
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar">
        <a href="/" className="navbar-brand">
          <i className="fas fa-box"></i> Estoque Fácil
        </a>
        <div className="navbar-nav">
          <a href="/">Login</a>
          <a href="/usuario">Cadastrar</a>
        </div>
      </nav>

      <div className="container-main">
        <div className="page-header">
          <h1>Bem-vindo ao Sistema</h1>
          <p>Faça login para gerenciar seu estoque</p>
        </div>

        <div className="card" style={{ maxWidth: '400px', margin: '0 auto' }}>
          <h2>Login</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Seu email"
                required
              />
            </div>
            <div className="form-group">
              <label>Senha</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Sua senha"
                required
              />
            </div>
            <button type="submit">Entrar</button>
          </form>
        </div>
      </div>

      <footer>
        <p>Sistema de Controle de Estoque &copy; 2025</p>
      </footer>
    </div>
  );
};

export default Login;
