import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Produto = () => {
  const [values, setValues] = useState({ name: '', quantidade: '' });
  const [produtos, setProdutos] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const nome = values.name.trim();
    const quantidade = parseInt(values.quantidade);

    if (!nome || isNaN(quantidade) || quantidade < 0) {
      alert('Nome e quantidade válidos são obrigatórios');
      return;
    }

    try {
      const response = await api.post('/cadastroProduto', {
        nameProduto: nome,
        quantidadeProduto: quantidade,
      });
      alert(response.data.message);
      setValues({ name: '', quantidade: '' });
      loadProdutos();
    } catch (error) {
      alert('Falha ao cadastrar produto');
    }
  };

  const loadProdutos = async () => {
    try {
      const response = await api.get('/produtos');
      setProdutos(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      alert('Erro ao carregar produtos');
    }
  };

  useEffect(() => {
    loadProdutos();
  }, []);

  return (
    <div>
      <nav className="navbar">
        <a href="/" className="navbar-brand">
          <i className="fas fa-box"></i> Estoque Fácil
        </a>
        <div className="navbar-nav">
          <a href="/">Login</a>
          <a href="/produto">Produtos</a>
          <a href="/usuario">Usuários</a>
          <a href="/venda">Vendas</a>
        </div>
      </nav>

      <div className="container-main">
        <div className="page-header">
          <h1>Gerenciamento de Produtos</h1>
          <p>Cadastre e atualize o estoque de produtos</p>
        </div>

        <div className="card">
          <h2>Cadastrar Produto</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Nome do Produto</label>
              <input
                type="text"
                name="name"
                value={values.name}
                onChange={handleChange}
                placeholder="Ex: Caneta, Caderno..."
                required
              />
            </div>
            <div className="form-group">
              <label>Quantidade em Estoque</label>
              <input
                type="number"
                name="quantidade"
                value={values.quantidade}
                onChange={handleChange}
                placeholder="0"
                required
                min="0"
              />
            </div>
            <button type="submit">Cadastrar / Atualizar</button>
          </form>
        </div>

        <div className="card">
          <h2>Produtos Cadastrados</h2>
          {produtos.length === 0 ? (
            <p className="message">Nenhum produto cadastrado.</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Produto</th>
                  <th>Estoque</th>
                </tr>
              </thead>
              <tbody>
                {produtos.map((p) => (
                  <tr key={p.id}>
                    <td>{p.nameProduto}</td>
                    <td>{p.quantidadeProduto}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>

      <footer>
        <p>Sistema de Controle de Estoque &copy; 2025</p>
      </footer>
    </div>
  );
};

export default Produto;
