import React from 'react';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import api from '../../services/api';

const Produto = () => {
  const [values, setValues] = useState({
    name: '',
    quantidade: '',
  });

  const alterarValores = (event) => {
    const { name, value } = event.target;
    setValues((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const createClickButton = async (event) => {
    event.preventDefault();
    try {
      const response = await api.post('/cadastroProduto', {
        nameProduto: values.name,
        quantidadeProduto: values.quantidade,
      });
      alert(response.data.message);
    } catch (error) {
      alert('Falha ao cadastrar produto');
    }
  };

  return 
const linkStyle = {
  margin: '0 10px',
  color: '#2575fc',
  textDecoration: 'none'
};  (
    <div className="Produto--container">
      <div className="registroProduto--container">
        <h2>Produtos</h2>
<nav style={{ marginBottom: '20px', textAlign: 'center' }}>
  <Link to="/" style={linkStyle}>Login</Link> | 
  <Link to="/produto" style={linkStyle}>Produtos</Link> | 
  <Link to="/usuario" style={linkStyle}>Usu√°rios</Link> | 
  <Link to="/venda" style={linkStyle}>Vendas</Link>
</nav>
        <br />
        <label>Nome do Produto</label>
        <input
          type="text"
          name="name"
          placeholder="Digite o nome do Produto"
          value={values.name}
          onChange={alterarValores}
          required
        />

        <br />
        <label>Quantidade</label>
        <input
          type="number"
          name="quantidade"
          placeholder="Digite a quantidade do Produto"
          value={values.quantidade}
          onChange={alterarValores}
          required
        />

        <br />
        <br />

        <button className="register--buttton" onClick={createClickButton}>
          Cadastrar / Atualizar
        </button>

        <br />
        <br />

        <Link to="/venda">Vender Produto</Link>
      </div>
    </div>
  );
};

export default Produto;
