import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const Usuario = () => {
  const [values, setValues] = useState({ name: '', email: '', password: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/auth/register', values);
      alert(response.data.message);
      if (response.data.code === 1) navigate('/');
    } catch (error) {
      alert('Falha ao registrar usuário');
    }
  };

  return (
    <div>
      <nav className="navbar">
        <a href="/" className="navbar-brand">
          <i className="fas fa-box"></i> Estoque Fácil
        </a>
        <div className="navbar-nav">
          <a href="/">Login</a>
          <a href="/usuario">Cadastrar</a>
        </div>
      </nav>

      <div className="container-main">
        <div className="page-header">
          <h1>Cadastro de Usuário</h1>
          <p>Registre um novo usuário para acessar o sistema</p>
        </div>

        <div className="card" style={{ maxWidth: '500px', margin: '0 auto' }}>
          <h2>Novo Usuário</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Nome</label>
              <input
                type="text"
                name="name"
                value={values.name}
                onChange={handleChange}
                placeholder="Nome completo"
                required
              />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={values.email}
                onChange={handleChange}
                placeholder="email@exemplo.com"
                required
              />
            </div>
            <div className="form-group">
              <label>Senha</label>
              <input
                type="password"
                name="password"
                value={values.password}
                onChange={handleChange}
                placeholder="Sua senha"
                required
              />
            </div>
            <button type="submit">Cadastrar</button>
          </form>
        </div>
      </div>

      <footer>
        <p>Sistema de Controle de Estoque &copy; 2025</p>
      </footer>
    </div>
  );
};

export default Usuario;
