import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Venda = () => {
  const [values, setValues] = useState({ name: '', quantidade: '' });
  const [vendas, setVendas] = useState([]);
  const [produtos, setProdutos] = useState([]);
  const [periodo, setPeriodo] = useState('todos');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const nome = values.name;
    const quantidade = parseInt(values.quantidade);

    if (!nome || isNaN(quantidade) || quantidade <= 0) {
      alert('Selecione um produto e quantidade v치lida');
      return;
    }

    try {
      const response = await api.post('/vendaProduto', {
        nameProduto: nome,
        quantidadeProduto: quantidade,
        usuario: sessionStorage.getItem('usuario'),
      });
      alert(response.data.message);
      setValues({ name: '', quantidade: '' });
      carregarVendas();
      carregarProdutos();
    } catch (error) {
      alert('Erro ao registrar venda');
    }
  };

  const handleDelete = async (nameProduto) => {
    if (window.confirm(`Excluir todas as vendas de "${nameProduto}"? Isso devolver치 o estoque.`)) {
      try {
        const response = await api.delete(`/venda?nameProduto=${encodeURIComponent(nameProduto)}`);
        alert(response.data.message);
        carregarVendas();
        carregarProdutos();
      } catch (error) {
        alert('Erro ao excluir venda');
      }
    }
  };

  const carregarVendas = async () => {
    try {
      const response = await api.get('/relatorioVenda');
      setVendas(Array.isArray(response.data?.produtos) ? response.data.produtos : []);
    } catch (error) {
      console.log('Erro ao carregar vendas');
    }
  };

  const carregarProdutos = async () => {
    try {
      const response = await api.get('/produtos');
      setProdutos(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      console.log('Erro ao carregar produtos');
    }
  };

  const logout = () => {
    sessionStorage.removeItem('usuario');
    window.location.href = '/';
  };

  useEffect(() => {
    carregarVendas();
    carregarProdutos();
  }, []);

  // --- FILTRO DE PER칈ODO ---
  const hoje = new Date();
  const vendasFiltradas = vendas.filter(v => {
    const data = new Date(v.dataHora);

    switch (periodo) {
      case 'hoje':
        return data.toDateString() === hoje.toDateString();
      case 'esta-semana':
        const diaSemana = hoje.getDay();
        const inicio = new Date(hoje);
        inicio.setDate(hoje.getDate() - diaSemana);
        return data >= inicio;
      case 'este-mes':
        return data.getMonth() === hoje.getMonth() && data.getFullYear() === hoje.getFullYear();
      default:
        return true;
    }
  });

  // --- VALOR TOTAL ---
  const totalValor = vendasFiltradas.reduce((sum, v) => sum + v.valorTotal, 0);

  return (
    <>
      {/* Navbar */}
      <nav style={navStyle}>
        <div style={navBrand}>游댱 Estoque F치cil</div>
        <div style={navLinks}>
          <a href="/produto" style={navLink}>Produtos</a>
          <a href="/venda" style={navLink}>Vendas</a>
          <span style={navUser}>Ol치, {sessionStorage.getItem('usuario')}</span>
          <button onClick={logout} style={logoutButton}>Sair</button>
        </div>
      </nav>

      <div style={pageContainer}>
        <h2 style={title}>Registrar Venda</h2>

        <form onSubmit={handleSubmit} style={form}>
          <div style={inputRow}>
            <div style={inputGroup}>
              <label style={labelStyle}>Produto</label>
              <select
                name="name"
                value={values.name}
                onChange={handleChange}
                required
                style={selectStyle}
              >
                <option value="">Selecione...</option>
                {produtos.map(p => (
                  <option key={p.id} value={p.nameProduto}>
                    {p.nameProduto} (R$ {p.valor?.toFixed(2)}) - Estoque: {p.quantidadeProduto}
                  </option>
                ))}
              </select>
            </div>

            <div style={inputGroup}>
              <label style={labelStyle}>Quantidade</label>
              <input
                type="number"
                name="quantidade"
                value={values.quantidade}
                onChange={handleChange}
                placeholder="1"
                required
                min="1"
                style={inputStyle}
              />
            </div>
          </div>

          <button type="submit" style={btnPrimary}>Vender</button>
        </form>

        <h2 style={title}>Hist칩rico de Vendas</h2>

        {/* Filtro */}
        <div style={filtroBox}>
          <label>Filtrar:</label>
          <select value={periodo} onChange={(e) => setPeriodo(e.target.value)} style={filtroSelect}>
            <option value="todos">Todos</option>
            <option value="hoje">Hoje</option>
            <option value="esta-semana">Esta Semana</option>
            <option value="este-mes">Este M칡s</option>
          </select>
        </div>

        {/* Resumo */}
        <div style={resumoBox}>
          <strong>Vendas:</strong> {vendasFiltradas.length} | 
          <strong> Total:</strong> R$ {totalValor.toFixed(2)}
        </div>

        {/* Tabela */} 
        <table style={tableStyle}>
          <thead>
            <tr>
              <th style={thStyle}>Produto</th>
              <th style={{ ...thStyle, width: '80px' }}>Quantidade</th>
              <th style={{ ...thStyle, width: '100px' }}>Unit치rio</th>
              <th style={{ ...thStyle, width: '100px' }}>Total</th>
              <th style={{ ...thStyle, width: '120px' }}>Usu치rio</th>
              <th style={{ ...thStyle, width: '120px' }}>Data</th>
              <th style={{ ...thStyle, width: '60px' }}>A칞칚o</th>
            </tr>
          </thead>
          <tbody>
            {vendasFiltradas.length === 0 ? (
              <tr>
                <td colSpan="7" style={{ textAlign: 'center', padding: '20px' }}>
                  Nenhuma venda encontrada
                </td>
              </tr>
            ) : (
              vendasFiltradas.map((v, k) => (
                <tr key={k}>
                  <td style={tdStyle}>{v.nameProduto}</td>
                  <td style={tdStyle}>{v.quantidadeProduto}</td>
                  <td style={tdStyle}>R$ {v.valorUnitario?.toFixed(2)}</td>
                  <td style={tdStyle}>R$ {v.valorTotal?.toFixed(2)}</td>
                  <td style={tdStyle}>{v.usuario}</td>
                  <td style={tdStyle}>{new Date(v.dataHora).toLocaleDateString()}</td>
                  <td style={tdStyle}></td>
                  <td>
                    <button onClick={() => handleDelete(v.nameProduto)} style={btnDelete}>
                      Excluir
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      <footer style={footerStyle}>
        Sistema de Controle de Estoque - Projeto Acad칡mico ADS
      </footer>
    </>
  );
};


const navStyle = {
  padding: '12px 20px',
  backgroundColor: '#ffffff',
  borderBottom: '1px solid #ddd',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  position: 'sticky',
  top: 0,
  zIndex: 100,
  boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
};

const navBrand = {
