import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Produto = () => {
  const [values, setValues] = useState({ name: '', quantidade: '' });
  const [produtos, setProdutos] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!values.name || !values.quantidade) {
      alert('Preencha todos os campos');
      return;
    }
    try {
      const response = await api.post('/cadastroProduto', {
        nameProduto: values.name,
        quantidadeProduto: parseInt(values.quantidade),
      });
      alert(response.data.message);
      setValues({ name: '', quantidade: '' });
      loadProdutos(); // Recarrega a lista
    } catch (error) {
      alert('Falha ao cadastrar produto');
    }
  };

  const loadProdutos = async () => {
    try {
      const response = await api.get('/produtos');
      setProdutos(response.data || []);
    } catch (error) {
      console.error('Erro ao carregar produtos', error);
      alert('Erro ao carregar produtos');
    }
  };

  useEffect(() => {
    loadProdutos();
  }, []);

  return (
    <div className="Produto--container">
      <div className="registroProduto--container">
        <nav>
          <a href="/">Login</a> | 
          <a href="/usuario">Usu√°rios</a> | 
          <a href="/produto">Produtos</a> | 
          <a href="/venda">Vendas</a>
        </nav>
        <h2>Produtos</h2>
        <form onSubmit={handleSubmit}>
          <label>Nome do Produto</label>
          <input
            type="text"
            name="name"
            placeholder="Nome do produto"
            value={values.name}
            onChange={handleChange}
            required
          />
          <br /><br />
          <label>Quantidade</label>
          <input
            type="number"
            name="quantidade"
            placeholder="Quantidade"
            value={values.quantidade}
            onChange={handleChange}
            required
            min="1"
          />
          <br /><br />
          <button className="register--buttton" type="submit">Cadastrar / Atualizar</button>
        </form>

        <br />
        <h3>Produtos Cadastrados</h3>
        {produtos.length === 0 ? (
          <p>Nenhum produto cadastrado</p>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {produtos.map(p => (
              <li key={p.id} style={{ margin: '8px 0', color: '#444' }}>
                <strong>{p.nameProduto}</strong>: {p.quantidadeProduto} unidades
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Produto;
